import React, { Component } from 'react'
import { fadeIn } from 'react-animations'
import styled, { keyframes ,css } from 'styled-components'
import '../CSS/App.css'
import { connect } from 'react-redux'
import { userScroll } from '../../Actions/scrollActions'

function mapStateToProps(store) {
  return { scrolling: store.pageLayout.scrolling };
}

const animationDelay = css`
  animation-delay:6s;
  opacity:0;
`

const FadeIn = styled.div`
  animation: ${keyframes`${fadeIn}`} 3s forwards;
  ${animationDelay}
`

class Hero extends Component {

  componentDidMount() {
    //Add script for title animation
    const script = document.createElement("script")
    script.src = "/firstPage.js"
    script.async = true
    script.type = "text/javascript"
    document.body.appendChild(script)
  }

  componentWillScroll(){
    this.props.dispatch(userScroll())
  }

  render(){
    console.log(this.props.scrolling)
    return (
    <div className="App" ref={el => (this.instance = el)} >
      <header className="App-header">
          <h1 className="overlay">
            <span>Alex Graham</span>
          </h1>
        <FadeIn>
          <p>
            Full Stack Developer
          </p>
        </FadeIn>
        <svg onClick={this.componentWillScroll.bind(this)} className="mouse scroll-link" xmlns="..." viewBox="0 0 76 130" preserveAspectRatio="xMidYmid meet">
          <g fill="none" fillRule="evenodd">
            <rect width="70" height="118" x="1.5" y="1.5" stroke="#FFF" strokeWidth="3" rx="36"/>
            <circle className="scroll" cx="36.5" cy="31.5" r="4.5" fill="#FFF"/>
          </g>
        </svg>
      </header>
    </div>
    )
  };
}

export default connect(mapStateToProps)(Hero)
