import React, { Component } from 'react'
import { fadeOut } from 'react-animations'
import styled, { keyframes, css } from 'styled-components'
import '../CSS/App.css'
import { connect } from 'react-redux'
import Hero from './heroComponent'
import { userScroll } from '../../Actions/scrollActions'

function mapStateToProps(store) {
  return { scrolling: store.pageLayout.scrolling };
}

const mapDispatchToProps = {
  userScroll
}

const FadeOut = styled.div`animation: ${keyframes`${fadeOut}`} 3s forwards;`

class Main extends Component {
  componentWillScroll(){
    this.props.dispatch(userScroll())
  }

  componentDidMount() {
    //Add script for title animation
    const script = document.createElement("script")
    script.src = "/firstPage.js"
    script.async = true
    script.type = "text/javascript"
    document.body.appendChild(script)
  }

  render(){
    if(!this.props.scrolling){
      return(
        <Hero scrolling={this.props.scrolling} store={store}></Hero>
      )
    }else{
      return(
        <FadeOut>
          <Hero scrolling={this.props.scrolling} store={store}></Hero>
        </FadeOut>
      )
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Main)
