export default function menuLayout(state={
    menuOpen: true,
    menuVisable: "container-full"
}, action) {
    switch (action.type){
        case "MENU_OPEN": {
            return {...state, menuOpen: action.payload, menuVisable: "container-reduced"}
        }
        case "MENU_CLOSE": {
            return {...state, menuOpen: action.payload, menuVisable: "container-full"}
        }
    }
    return state
}
